class App {
    constructor() {
        this.foods = [
            {
                id: 1,
                name: "兰州牛肉拉面",
                category: "面食",
                location: "梅川路近真光路",
                rating: 4.3,
                reviews: 127,
                image: "https://via.placeholder.com/300x200",
                description: "正宗兰州拉面，汤清面劲，牛肉香浓，学生最爱的平价美食"
            },
            {
                id: 2,
                name: "华莱士",
                category: "快餐",
                location: "真光路梅川路口",
                rating: 4.1,
                reviews: 89,
                image: "https://via.placeholder.com/300x200",
                description: "平价汉堡薯条，学生聚餐首选，性价比很高"
            },
            {
                id: 3,
                name: "蜜雪冰城",
                category: "饮品",
                location: "梅川路商业街",
                rating: 4.6,
                reviews: 156,
                image: "https://via.placeholder.com/300x200",
                description: "柠檬水和冰淇淋超赞，价格便宜，学生党最爱"
            },
            {
                id: 4,
                name: "沙县小吃",
                category: "小吃",
                location: "真光路",
                rating: 4.2,
                reviews: 78,
                image: "https://via.placeholder.com/300x200",
                description: "经典的沙县三宝，拌面蒸饺炖罐，便宜实惠管饱"
            },
            {
                id: 5,
                name: "正新鸡排",
                category: "小吃",
                location: "梅川路步行街",
                rating: 4.4,
                reviews: 134,
                image: "https://via.placeholder.com/300x200",
                description: "现炸鸡排，外酥内嫩，各种口味选择多"
            },
            {
                id: 6,
                name: "桂林米粉",
                category: "面食",
                location: "真北路",
                rating: 4.0,
                reviews: 65,
                image: "https://via.placeholder.com/300x200",
                description: "地道桂林米粉，酸辣开胃，汤头鲜美"
            },
            {
                id: 7,
                name: "书亦烧仙草",
                category: "饮品",
                location: "梅川路",
                rating: 4.5,
                reviews: 92,
                image: "https://via.placeholder.com/300x200",
                description: "烧仙草和奶茶都很棒，配料丰富，颜值很高"
            },
            {
                id: 8,
                name: "煎饼果子摊",
                category: "早餐",
                location: "学校门口",
                rating: 4.7,
                reviews: 203,
                image: "https://via.placeholder.com/300x200",
                description: "早餐必选，现做现卖，香脆可口，老板手艺好"
            },
            {
                id: 9,
                name: "麦当劳",
                category: "快餐",
                location: "真如副中心",
                rating: 4.2,
                reviews: 245,
                image: "https://via.placeholder.com/300x200",
                description: "经典快餐，环境舒适，适合聚会学习"
            },
            {
                id: 10,
                name: "老盛昌汤包",
                category: "小吃",
                location: "真光路",
                rating: 4.3,
                reviews: 118,
                image: "https://via.placeholder.com/300x200",
                description: "上海老字号，汤包皮薄汁多，还有各种馄饨面条"
            },
            {
                id: 11,
                name: "重庆小面",
                category: "面食",
                location: "梅川路",
                rating: 4.1,
                reviews: 87,
                image: "https://via.placeholder.com/300x200",
                description: "麻辣鲜香的重庆小面，口味正宗，分量足"
            },
            {
                id: 12,
                name: "一点点奶茶",
                category: "饮品",
                location: "真如商圈",
                rating: 4.6,
                reviews: 167,
                image: "https://via.placeholder.com/300x200",
                description: "台式奶茶品牌，波霸奶茶和四季春茶特别受欢迎"
            }
        ];
        this.init();
    }

    init() {
        this.render();
        this.bindEvents();
    }

    render() {
        const root = document.getElementById('root');
        root.innerHTML = `
            <header class="header">
                <h1>🍔 曹杨二中周边美食评分</h1>
                <p>发现真光路·梅川路商圈最好吃的美食！</p>
            </header>

            <main class="main">
                <div class="search-section">
                    <input type="text" id="searchInput" placeholder="搜索美食名称或类别..." class="search-input">
                    <button id="searchBtn" class="search-btn">搜索</button>
                </div>

                <div class="filter-section">
                    <button class="filter-btn active" data-category="all">全部</button>
                    <button class="filter-btn" data-category="面食">面食</button>
                    <button class="filter-btn" data-category="快餐">快餐</button>
                    <button class="filter-btn" data-category="饮品">饮品</button>
                    <button class="filter-btn" data-category="小吃">小吃</button>
                    <button class="filter-btn" data-category="早餐">早餐</button>
                </div>

                <div class="food-grid" id="foodGrid">
                    ${this.renderFoodCards()}
                </div>

                <button class="add-review-btn" id="addReviewBtn">+ 添加新发现的美食</button>
            </main>

            <div class="modal" id="reviewModal" style="display: none;">
                <div class="modal-content">
                    <span class="close">&times;</span>
                    <h2>添加美食评价</h2>
                    <form id="reviewForm">
                        <input type="text" name="name" placeholder="美食名称" required>
                        <select name="category" required>
                            <option value="">选择类别</option>
                            <option value="面食">面食</option>
                            <option value="快餐">快餐</option>
                            <option value="饮品">饮品</option>
                            <option value="小吃">小吃</option>
                            <option value="早餐">早餐</option>
                            <option value="其他">其他</option>
                        </select>
                        <input type="text" name="location" placeholder="具体位置（如：真光路、梅川路等）" required>
                        <select name="rating" required>
                            <option value="">评分</option>
                            <option value="5">5星 - 超级推荐</option>
                            <option value="4">4星 - 很好吃</option>
                            <option value="3">3星 - 还可以</option>
                            <option value="2">2星 - 不推荐</option>
                            <option value="1">1星 - 很难吃</option>
                        </select>
                        <textarea name="description" placeholder="分享一下这家店的特色和你的用餐体验..." required></textarea>
                        <button type="submit">分享给同学们</button>
                    </form>
                </div>
            </div>
        `;
    }

    renderFoodCards() {
        return this.foods.map(food => `
            <div class="food-card" data-category="${food.category}">
                <img src="${food.image}" alt="${food.name}" class="food-image">
                <div class="food-info">
                    <h3 class="food-name">${food.name}</h3>
                    <p class="food-category">${food.category}</p>
                    <p class="food-location">📍 ${food.location}</p>
                    <div class="rating">
                        <span class="stars">${this.renderStars(food.rating)}</span>
                        <span class="rating-number">${food.rating}</span>
                        <span class="review-count">(${food.reviews}条评价)</span>
                    </div>
                    <p class="food-description">${food.description}</p>
                </div>
            </div>
        `).join('');
    }

    renderStars(rating) {
        const fullStars = Math.floor(rating);
        const hasHalfStar = rating % 1 !== 0;
        let starsHTML = '';

        for (let i = 0; i < fullStars; i++) {
            starsHTML += '⭐';
        }
        if (hasHalfStar) {
            starsHTML += '✨';
        }

        return starsHTML;
    }

    bindEvents() {
        // 搜索功能
        const searchInput = document.getElementById('searchInput');
        const searchBtn = document.getElementById('searchBtn');

        searchBtn.addEventListener('click', () => this.handleSearch());
        searchInput.addEventListener('keypress', (e) => {
            if (e.key === 'Enter') {
                this.handleSearch();
            }
        });

        // 筛选功能
        const filterBtns = document.querySelectorAll('.filter-btn');
        filterBtns.forEach(btn => {
            btn.addEventListener('click', (e) => {
                filterBtns.forEach(b => b.classList.remove('active'));
                e.target.classList.add('active');
                this.handleFilter(e.target.dataset.category);
            });
        });

        // 添加评价模态框
        const addReviewBtn = document.getElementById('addReviewBtn');
        const modal = document.getElementById('reviewModal');
        const closeBtn = document.querySelector('.close');
        const reviewForm = document.getElementById('reviewForm');

        addReviewBtn.addEventListener('click', () => {
            modal.style.display = 'block';
        });

        closeBtn.addEventListener('click', () => {
            modal.style.display = 'none';
        });

        window.addEventListener('click', (e) => {
            if (e.target === modal) {
                modal.style.display = 'none';
            }
        });

        reviewForm.addEventListener('submit', (e) => {
            e.preventDefault();
            this.handleAddReview(e);
        });
    }

    handleSearch() {
        const searchTerm = document.getElementById('searchInput').value.toLowerCase();
        const foodCards = document.querySelectorAll('.food-card');

        foodCards.forEach(card => {
            const foodName = card.querySelector('.food-name').textContent.toLowerCase();
            const foodCategory = card.querySelector('.food-category').textContent.toLowerCase();
            const foodLocation = card.querySelector('.food-location').textContent.toLowerCase();

            if (foodName.includes(searchTerm) || 
                foodCategory.includes(searchTerm) || 
                foodLocation.includes(searchTerm)) {
                card.style.display = 'block';
            } else {
                card.style.display = 'none';
            }
        });
    }

    handleFilter(category) {
        const foodCards = document.querySelectorAll('.food-card');

        foodCards.forEach(card => {
            if (category === 'all' || card.dataset.category === category) {
                card.style.display = 'block';
            } else {
                card.style.display = 'none';
            }
        });
    }

    handleAddReview(e) {
        const formData = new FormData(e.target);
        const newFood = {
            id: Date.now(),
            name: formData.get('name'),
            category: formData.get('category'),
            location: formData.get('location'),
            rating: parseFloat(formData.get('rating')),
            reviews: 1,
            image: "https://via.placeholder.com/300x200",
            description: formData.get('description')
        };

        this.foods.push(newFood);
        document.getElementById('foodGrid').innerHTML = this.renderFoodCards();
        document.getElementById('reviewModal').style.display = 'none';
        e.target.reset();

        // 重新绑定事件（因为DOM重新渲染了）
        this.bindEvents();

        alert('谢谢分享！你的美食推荐已经添加成功！');
    }
}

// 启动应用
document.addEventListener('DOMContentLoaded', () => {
    new App();
});
